/**
 * An implementation of the Knuth-Shuffle algorithm, which can complete the shuffle in $O(N)$ time
 * complexity on the basis of only using a constant level of extra space.
 *
 * @param elements
 * @see https://me.guanghechen.com/post/algorithm/shuffle/#heading-knuth-shuffle
 */
declare function knuthShuffle<T = unknown>(elements: T[], start?: number, end?: number): void;

/**
 * Create a random integer in the range of [0, n)
 * @param n
 */
declare function randomInt(n: number): number;

export { knuthShuffle as default, knuthShuffle, randomInt };
